			["customText"] = "function()\n    local unit = \"target\"\n    local health = UnitHealth(unit)\n    local healthP = string.format(\"%.1f\",(health/UnitHealthMax(unit)) * 100)\n    local power = UnitPower(unit)\n    local powerP = string.format(\"%.1f\",(power/UnitPowerMax(unit)) * 100)\n    local healthR = \"\"\n    local powerR = \"\"\n    \n    function trunc(n)\n        if (n >= 30000 and n < 1000000) then\n            return string.format(\"%.1fk\", n / 1000)\n        elseif (n >= 1000000) then\n            return string.format(\"%.1fm\", n / 1000000)\n        end\n        return n\n    end\n    \n    health = trunc(health)\n    power = trunc(power)\n    \n    if aura_env.config[\"ShowPlayerHealthNumbers\"] then\n        healthR = healthR .. health\n    end\n    if(healthR ~= \"\") then\n        healthR = healthR .. \" \"\n    end\n    if aura_env.config[\"ShowPlayerHealthPercent\"] then\n        healthR = healthR .. math.floor(healthP)\n        if health == 0 then\n            healthR = \"Dead\"\n        end\n    end\n    \n    if aura_env.config[\"ShowPlayerPowerNumbers\"] then\n        powerR = powerR .. power\n    end\n    if(powerR ~= \"\") then\n        powerR = powerR .. \" \"\n    end\n    \n    if aura_env.config[\"ShowPlayerPowerPercent\"] then\n        powerR = powerR .. math.floor(powerP)\n        if UnitPowerMax(unit) == 0 then\n            powerR = \"\"\n        end\n    end\n    \n    return health,healthP,power,powerP,UnitName(unit)\nend",
