					["custom"] = "local parentName = WeakAurasSaved.displays[WeakAuras.regions[aura_env.id].region:GetParent().id]\nif (parentName[\"TehrsRaidCDs\"] == nil) then parentName[\"TehrsRaidCDs\"] = {} end    \nlocal TehrsCDs = parentName[\"TehrsRaidCDs\"]    \n\naura_env.getPetOwner = function(petName, petGUID)\n    local ownerName\n    \n    if UnitGUID(\"pet\") == petGUID then\n        ownerName = GetUnitName(\"player\")\n    elseif IsInRaid() then\n        for i=1, GetNumGroupMembers() do\n            if UnitGUID(\"raid\"..i..\"pet\") == petGUID then\n                ownerName = GetUnitName(\"raid\"..i)\n                break\n            end\n        end\n    else\n        for i=1, GetNumSubgroupMembers() do\n            if UnitGUID(\"party\"..i..\"pet\") == petGUID then\n                ownerName = GetUnitName(\"party\"..i)\n                break\n            end\n        end\n    end\n    \n    if ownerName then\n        return ownerName\n    else\n        return petName\n    end\nend   \n\n-- This is just here so I can debug when shit hits the fan, you will never need to enable this\nTehrsCDs.DEBUG_Engine = false",
