					["translateFunc"] = "function(progress, startX, startY, deltaX, deltaY)\n    if aura_env and aura_env.state then\n        local aura_env = aura_env\n        if aura_env.manual then\n            return aura_env.positions[aura_env.cloneId].x, aura_env.positions[aura_env.cloneId].y\n        else\n            if aura_env.layout == \"box\" then\n                local value = -(aura_env.cloneId-1)*(aura_env.region:GetHeight()+aura_env.Spacing) or 0\n                local valueX = 0\n                if aura_env.cloneId > 3 then\n                    value = -(aura_env.cloneId-4)*(aura_env.region:GetHeight()+aura_env.Spacing)\n                    valueX =  (aura_env.region:GetWidth()+aura_env.Spacing)\n                end\n                return valueX, value\n            else\n                \n                local value = (aura_env.cloneId-1)*(aura_env.region:GetWidth()+aura_env.Spacing) or 0\n                return value, 0\n            end\n        end\n    end\nend",
