				["start"] = {
					["do_loop"] = true,
					["custom"] = "if not aura_env.config.sound and not aura_env.config.icon then return end\n\nlocal region = aura_env.region\nlocal bar = region.bar\nlocal width, height = aura_env.width, aura_env.height\n\n-- handle \"Set Maximum Progress\"\nlocal aura_duration = not region.adjustedMax and aura_env.state.duration or region.adjustedMax\nlocal aura_expirationTime = aura_env.state.expirationTime\n\nlocal haste = GetHaste(\"player\") / 100\nlocal gcd_duration = 1.5 / (haste + 1)\n\nlocal _,_,latency = GetNetStats()\nlatency = latency / 1000 + aura_env.tconfig.brain_lag\n\n\n-- disc rotation helper\nif aura_env.config.helpers[1] and aura_env.can_use_spell(17) then\n    -- [[ pre burst ]] ----------------------\n    local preburst_timeline = {\n        [1] = {\n            name = \"radiance\",\n            active = true,\n            duration = 2 / (haste + 1),\n            spellid = 194509,\n            icon = select(3, GetSpellInfo(194509)),\n            charge = 2,\n            sound = [[Interface\\Addons\\WeakAuras\\Media\\Sounds\\RobotBlip.ogg]]\n        },\n        [2] = {\n            name = \"radiance\",\n            active = true,\n            duration = 2 / (haste + 1),\n            spellid = 194509,\n            icon = select(3, GetSpellInfo(194509)),\n            charge = 1,\n            sound = [[Interface\\Addons\\WeakAuras\\Media\\Sounds\\RobotBlip.ogg]]\n        },\n        [3] = {\n            name = \"evangelism\",\n            active = select(4, GetTalentInfo(7, 3, 1)),\n            duration = 1.5 / (haste + 1),\n            spellid = 246287,\n            icon = select(3, GetSpellInfo(246287)),\n            sound = nil\n        },\n        [4] = {\n            name = \"shadowfiend\",\n            active = not select(4, GetTalentInfo(3, 2, 1)),\n            duration = gcd_duration,\n            spellid = 34433,\n            icon = select(3, GetSpellInfo(34433)),\n            sound = nil\n        },\n        [5] = {\n            name = \"mindbender\",\n            active = select(4, GetTalentInfo(3, 2, 1)),\n            duration = gcd_duration,\n            spellid = 123040,\n            icon = select(3, GetSpellInfo(123040)),\n            sound = nil\n        },\n        [6] = {\n            name = \"schism\",\n            active = select(4, GetTalentInfo(1, 3, 1)),\n            duration = 1.5 / (haste + 1),\n            spellid = 214621,\n            icon = select(3, GetSpellInfo(214621)),\n            charge = nil,\n            sound = nil\n        }\n    }\n    local spells = preburst_timeline\n    if aura_env.config.consider_latency then\n        for i=1,#spells do\n            local spell = spells[i]\n            spell.duration = spell.duration + latency\n        end \n    end\n    \n    \n    -- set start & active\n    local start = 0\n    for i=#spells,1,-1 do\n        local spell = spells[i]\n        if spell.active then\n            if aura_env.can_use_spell(spell.spellid, aura_duration + start - spell.duration, spell.charge) then\n                start = start - spell.duration\n            else\n                spell.active = false\n            end\n        end\n        spell.start = start\n    end\n    \n    --setup sounds\n    if aura_env.config.sound then\n        for _,spell in pairs(spells) do\n            if spell.active and spell.sound then\n                local soundfile = spell.sound\n                local timer = aura_duration + spell.start\n                if timer > 0 then\n                    local timer = C_Timer.NewTimer(\n                        timer, \n                        function() PlaySoundFile(soundfile, \"master\") end\n                    )\n                    table.insert(aura_env.soundTimers, timer)\n                end\n            end\n        end\n    end\n    \n    -- add icons to progressbar\n    if aura_env.config.icon then\n        for i=1,#preburst_timeline do\n            local spell = preburst_timeline[i]\n            if spell.active then\n                local icon_size = height * aura_env.tconfig.icon_height_pct\n                local spark_coord_x = (width/aura_duration)*(aura_duration+spell.start)\n                local icon_coord_x = (width/aura_duration)*(aura_duration+spell.start+spell.duration/2)-icon_size/2\n                local spark = bar[\"spark\"..i]\n                if not spark then\n                    spark = aura_env.pool:Acquire()\n                end\n                spark:SetTexture([[Interface\\AddOns\\WeakAuras\\Media\\Textures\\Square_White]])\n                spark:SetSize(1, height-4)\n                spark:SetVertexColor(1, 1, 1, 0.2)\n                spark:SetPoint(\"LEFT\", spark_coord_x,0)\n                spark:Show()\n                local icon = bar[\"icon\"..i]\n                if not icon then\n                    icon = aura_env.pool:Acquire()\n                end\n                icon:SetSize(icon_size, icon_size)\n                icon:SetTexture(spell.icon)\n                icon:SetAlpha(0.7)\n                icon:SetPoint(\"LEFT\", icon_coord_x, 0)\n                icon:Show()\n            end\n        end\n    end\nend\n\n\nfunction singleCastHelper(spellId, baseTime, static_position)\n    local casttime = baseTime/(1+haste/100) + latency\n    if aura_env.can_use_spell(spellId, aura_duration - (static_position or 0)) then\n        local icon_size = height * aura_env.tconfig.icon_height_pct\n        local time_from_end = casttime+latency+(static_position or 0)\n        local spark_coord_x = (width/aura_duration)*(aura_duration-time_from_end)\n        local icon_coord_x = (width/aura_duration)*(aura_duration-time_from_end+casttime/2)-icon_size/2\n        local spark = bar[\"spark\"..spellId]\n        if not spark then\n            spark = aura_env.pool:Acquire()\n        end\n        spark:SetTexture([[Interface\\AddOns\\WeakAuras\\Media\\Textures\\Square_White]])\n        spark:SetSize(1, height-4)\n        spark:SetVertexColor(1, 1, 1, 0.2)\n        spark:SetPoint(\"LEFT\", spark_coord_x,0)\n        spark:Show()\n        local icon = bar[\"icon\"..spellId]\n        if not icon then\n            icon = aura_env.pool:Acquire()\n        end\n        icon:SetSize(icon_size, icon_size)\n        icon:SetTexture(select(3, GetSpellInfo(spellId)))\n        icon:SetAlpha(0.7)\n        icon:SetPoint(\"LEFT\", icon_coord_x, 0)\n        icon:Show()\n        return\n    end\nend\n\nif aura_env.config.icon then\n    if aura_env.config.helpers[2] then\n        singleCastHelper(265202, 2.5) -- Holy Word: Salvation\n    end\n    if aura_env.config.helpers[3] then\n        singleCastHelper(123986, 1)   -- Chi Burst\n    end\n    if aura_env.config.helpers[4] then\n        singleCastHelper(48438, 1.5)  -- Wild Growth\n    end\n    if aura_env.config.helpers[5] then\n        singleCastHelper(296072, gcd_duration, 8 - gcd_duration) -- Ever-Rising Tide rank 1\n        singleCastHelper(299875, gcd_duration, 8 - gcd_duration) -- Ever-Rising Tide rank 2\n        singleCastHelper(299876, gcd_duration, 8 - gcd_duration) -- Ever-Rising Tide rank 3\n    end\nend",
