						["custom"] = "function(allstates, event, unit, ...)\n    \n    --UNIT_HEALTH, CLEU:SPELL_AURA_APPLIED:SPELL_AURA_REMOVED:SPELL_ABSORBED\n    \n    --Setup aura_env variable\n    local e = aura_env\n    \n    if UnitExists(e.bossUnit) then\n        \n        local bossGUID = UnitGUID(e.bossUnit)\n        \n        if e.skinActive then \n            \n            if event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n                \n                --Get values from CLEU\n                local subEvent, hideCaster, sourceGUID, sourceName, sourceFlags, sourceRaidFlags,\n                destGUID, destName, destFlags, destRaidFlags, spellID, _, _, _, amount = ... \n                \n                if subEvent == \"SPELL_AURA_REMOVED\" and destGUID == bossGUID and spellID == e.auraID then\n                    \n                    --End aura\n                    e.skinActive = false\n                    \n                    if e.config.debug then print(\"Mana Bar: Obsidian Skin Removed\") end\n                    \n                    --Hide progress bar\n                    if allstates[e.bossUnit] then\n                        allstates[e.bossUnit].show = false\n                        allstates[e.bossUnit].changed = true\n                    end\n                elseif subEvent == \"SPELL_ABSORBED\" and destGUID == bossGUID then\n                    \n                    e.bossManaCurrent = UnitPower(e.bossUnit,e.powerType)\n                    e.timeElapsed = GetTime() - e.startTimer\n                    e.timeLeft = e.phaseLength - e.timeElapsed\n                    e.raidDPS = (e.bossManaStart-e.bossManaCurrent)/e.timeElapsed\n                    \n                    if e.raidDPS >= e.dpsTarget then\n                        e.actual = \"+\"..ShortenNumber(e.raidDPS - e.dpsTarget,2)..\" DPS\"\n                        e.colour = \"GREEN\"\n                    elseif e.raidDPS < e.dpsTarget then\n                        e.actual = \"-\"..ShortenNumber(e.raidDPS - e.dpsTarget,2)..\" DPS\"\n                    e.colour = \"RED\"\n                else\n                    e.actual = \"...\"\n                    e.colour = \"BLUE\"\n                end\n                \n                --Update bar\n                if allstates[e.bossUnit] then\n                    allstates[e.bossUnit].value = UnitPower(e.bossUnit,e.powerType)\n                    allstates[e.bossUnit].actual = e.actual\n                    allstates[e.bossUnit].changed = true\n                end   \n            end   \n        end       \n    else\n        if event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n            \n            --Get values from CLEU\n            local subEvent, hideCaster, sourceGUID, sourceName, sourceFlags, sourceRaidFlags,\n            destGUID, destName, destFlags, destRaidFlags, spellID, _, _, _, amount = ... \n            \n            if subEvent == \"SPELL_AURA_APPLIED\" and destGUID == bossGUID and spellID == e.auraID then\n                \n                if e.config.debug then print(\"Mana Bar: Obsidian Skin Applied\") end\n                \n                e.skinActive = true\n                e.bossManaStart = UnitPower(e.bossUnit,e.powerType)\n                e.startTimer = GetTime()\n                e.dpsTarget = e.bossManaStart/e.phaseLength\n                e.target = \"T: \"..ShortenNumber(e.bossManaStart/e.phaseLength,2)\n                e.actual = \"...\"\n                \n                if e.config.debug then print(\"Mana Bar: Mana Start \"..ShortenNumber(e.bossManaStart,1)) end\n                \n                --Show progress bar\n                allstates[e.bossUnit] = {\n                    show = true,\n                    changed = true,\n                    value = UnitPower(e.bossUnit,e.powerType),\n                    total = UnitPowerMax(e.bossUnit),\n                    progressType = \"static\",\n                    name = UnitName(e.bossUnit),\n                    target = e.target,\n                    actual = e.actual\n                } \n            end    \n        end       \n    end\nend\n\nreturn true\nend",
