				["init"] = {
					["custom"] = "-- Eternal Palace Burst Timers\n-- last edit: 11/07\n-- EternalPalace_burst_timers\n-- Contact Buds @ https://discord.gg/wa2\n\naura_env.tconfig = {\n    debug = false,\n    brain_lag = 0.1,\n    icon_height_pct = 0.8, -- 1 => icon=bar_height, 0.5 => icon=bar_height/2\n}\n\naura_env.soundTimers = {}\n\nif type(budsPool) ~= \"table\" then\n    budsPool = {}\n    hooksecurefunc(WeakAuras, \"OpenOptions\", function()\n            for k in pairs(budsPool) do\n                budsPool[k]:ReleaseAll()\n            end\n        end\n    )    \nend\nif not budsPool[aura_env.id] then\n    budsPool[aura_env.id] = CreateTexturePool(aura_env.region.bar, \"OVERLAY\")\nend\naura_env.pool = budsPool[aura_env.id]\n\nWeakAuras.WatchGCD()\n\ndo\n    local data = WeakAuras.GetData(aura_env.id)\n    aura_env.width = data.width - data.height\n    aura_env.height = data.height\nend\n\n\nfunction aura_env.can_use_spell(spellId, time, charge)\n    -- time => check if spell will be ready in x seconds, or now if nill\n    -- charge => number of the charge to check (or nil)\n    if IsSpellKnown(spellId) then\n        if not charge then\n            local started,cd = GetSpellCooldown(spellId)\n            if cd > 0 and cd ~= WeakAuras.gcdDuration() then\n                if time then\n                    return GetTime()+time >= started+cd\n                end\n            else\n                return true\n            end\n        else\n            -- spell with charges\n            local currentCharges, maxCharges, started, cd = GetSpellCharges(spellId)\n            if currentCharges >=  charge then\n                return true\n            else\n                if time then\n                    return (charge - currentCharges) * cd - (GetTime() - started) <= time\n                else\n                    return false\n                end\n            end\n        end\n    end\n    return false\nend\n\nfunction aura_env.countAzeriteTrait(spellId)\n    local count = 0\n    local azeriteItemLocation = C_AzeriteItem.FindActiveAzeriteItem()\n    if (not azeriteItemLocation) then return 0 end\n    \n    local azeritePowerLevel = C_AzeriteItem.GetPowerLevel(azeriteItemLocation)\n    local specID = GetSpecializationInfo(GetSpecialization())\n    \n    for _,slot in next, { 1, 3, 5 } do\n        local item = Item:CreateFromEquipmentSlot(slot)\n        if (not item:IsItemEmpty()) then\n            local itemLocation = item:GetItemLocation()\n            if (C_AzeriteEmpoweredItem.IsAzeriteEmpoweredItem(itemLocation)) then\n                local tierInfo = C_AzeriteEmpoweredItem.GetAllTierInfo(itemLocation)\n                for tier, info in next, tierInfo do\n                    if (info.unlockLevel <= azeritePowerLevel) then\n                        for _, powerID in next, info.azeritePowerIDs do\n                            if C_AzeriteEmpoweredItem.IsPowerSelected(itemLocation, powerID)\n                            and C_AzeriteEmpoweredItem.IsPowerAvailableForSpec(powerID, specID)\n                            then\n                                local powerInfo = C_AzeriteEmpoweredItem.GetPowerInfo(powerID)\n                                if powerInfo.spellID == spellId then\n                                    count = count + 1\n                                end\n                            end\n                        end\n                    end\n                end\n            end\n        end\n    end\n    return count\nend",
