            while e.healthTable[i] do\n                    local time = e.healthTable[i][1]\n                    \n                    if currentTime > time + e.config.seconds then\n                        table.remove(e.healthTable,i)\n                    else\n                        i = i + 1\n                    end\n                end\n                \n                --Calculate health variance\n                local sum = 0\n                local elements = 0\n                \n                for k,v in ipairs(e.healthTable) do\n                    for k1, v1 in ipairs(v) do\n                        sum = sum + v1\n                        elements = elements + 1\n                    end\n                end\n                \n                --Calculate health stats\n                e.playerHealthVariance = math.ceil(sum/elements)\n                e.playerHealthTrend = e.currentPlayerHealth + e.playerHealthVariance\n                e.playerHealthTrendPercent = math.ceil((e.playerHealthTrend/UnitHealthMax(e.playerUnit))*100)\n                \n                if e.config.debug then print(\"Player Bar - Trend: \"..e.playerHealthTrend..\" - Trend %: \"..e.playerHealthTrendPercent..\"% - Elements: \"..elements) end\n                \n                --Format health variance text\n                if e.playerHealthVariance > 0 then\n                    e.playerHealthVarianceText = \"+\"..ShortenNumber(e.playerHealthVariance,1)..\" HPS\"\n                else\n                    e.playerHealthVarianceText = \"-\"..ShortenNumber(e.playerHealthVariance,1)..\" HPS\"\n                end\n                \n                --Update bar colour and text\n                if e.playerHealthTrendPercent < e.config.stop then\n                    e.colour = \"RED\"\n                    e.statusText = \"STOP\"\n                elseif e.playerHealthTrendPercent < e.config.slow then\n                    e.colour = \"YELLOW\"\n                    e.statusText = \"SLOW\"\n                else\n                    e.colour = \"BLUE\"\n                    e.statusText = \"BLAST\"\n                end\n                \n                --Update bar\n                if allstates[e.playerUnit] then\n                    allstates[e.playerUnit].value = UnitHealth(e.playerUnit)\n                    allstates[e.playerUnit].total = UnitHealthMax(e.playerUnit)\n                    allstates[e.playerUnit].text = e.statusText\n                    allstates[e.playerUnit].changed = true\n                end   \n            end       \n        else\n            if event == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n                \n                --Get values from CLEU\n                local subEvent, hideCaster, sourceGUID, sourceName, sourceFlags, sourceRaidFlags,\n                destGUID, destName, destFlags, destRaidFlags, spellID, _, _, _, amount = ... \n                \n                if subEvent == \"SPELL_AURA_APPLIED\" and destGUID == bossGUID and spellID == e.auraID then\n                    \n                    if e.config.debug then print(\"Player Bar - Obsidian Skin Applied\") end\n                    \n                    --Reset variables\n                    e.skinActive = true\n                    e.statusText = \"...\"\n                    e.healthTable = {}\n                    e.currentPlayerHealth = UnitHealth(e.playerUnit)\n                    e.playerHealthVariance = 0\n                    e.healthDiff = 0\n                    \n                    --Show progress bar\n                    allstates[e.playerUnit] = {\n                        show = true,\n                        changed = true,\n                        value = UnitHealth(e.playerUnit),\n                        total = UnitHealthMax(e.playerUnit),\n                        progressType = \"static\",\n                        name = UnitName(e.playerUnit),\n                        text = e.statusText\n                    } \n                end    \n            end       \n        end\n    end\n    \n    return true\nend\n\n\n\n",
